sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var config = current.u_export.getRefRecord();
	if(current.u_direction == "pull") {
		config.setValue("u_latest_run_pull", current.getUniqueValue());
		if(config.u_latest_run_push == current.getUniqueValue()) {
			config.setValue("u_latest_run_push", "");
		}
	}
	else if(current.u_direction == "push") {
		config.setValue("u_latest_run_push", current.getUniqueValue());
		if(config.u_latest_run_pull == current.getUniqueValue()) {
			config.setValue("u_latest_run_pull", "");
		}
	}

	config.update();

})(current, previous);
