sys_variable_value.value:
(function execute(inputs, outputs) {

  var response = JSON.parse(inputs.responseJSON);

  if(response.errors) {
    throw "Failed query: " + response.errors[0].message;
  }

  else if(response.data) {
    if(!response.data.repository.ref) {
     throw "Failed to find branch with the name '" + inputs.originalBranchName + "' within the repository.";
    }
    outputs.repo_name = response.data.repository.name;
    outputs.repo_url = response.data.repository.url;
    outputs.branch_name = response.data.repository.ref.name;
    outputs.commit_oid = response.data.repository.ref.target.oid + "";

  }

})(inputs, outputs);

