sys_variable_value.value:
(function execute(inputs, outputs) {

  var response = JSON.parse(inputs.responseJSON);

  if(response.message) {
    throw "Failed to get commit details: " + response.message;
  }

  outputs.commit_oid = response.sha;
  outputs.commit_url = response.html_url;
  outputs.commit_date = response.commit.committer.date;
  outputs.commit_message = response.commit.message;

  var files = response.files;
  for(var i=0; i<files.length; i++) {
    var file = files[i];
    //delete file.raw_url;
    switch(file.status) {
      case "modified":
      case "renamed":
      case "changed":
        file.status = "updated";
        break;
      case "added":
      case "copied":
        file.status = "created";
        break;
      case "removed":
        file.status = "deleted";
        break;
      case "unchanged":
        file.status = "unchanged";
        break;
    }
  }

    outputs.files = files;

})(inputs, outputs);

