sys_variable_value.value:
(function execute(inputs, outputs) {

  var exporter = new global.PDIExporterUtils(inputs.config);
  exporter.runXMLExport();

  outputs.content1 = exporter.getOutput();
  outputs.file_name = exporter.getFileName();

})(inputs, outputs);

