sys_variable_value.value:
(function execute(inputs, outputs) {

  if(!inputs.table || !inputs.record_id) return;

  var gr = new GlideRecord(inputs.table);
  if(!gr.get(inputs.record_id)) {
     throw "Could not find record " + inputs.table + "/" + inputs.record_id;
  }

  try {
  var rawExtension = inputs.name.split(".");
  rawExtension = rawExtension[rawExtension.length-1].toString();
  var contentType = getMimeType(rawExtension);
  } catch(e) {
   gs.warn("Failed to convert content type");
  }

  var existing = new GlideRecord("sys_attachment");
  existing.addQuery("table_name", inputs.table);
  existing.addQuery("table_sys_id", inputs.record_id);
  existing.addQuery("file_name", inputs.name);
  existing.query();
  while(existing.next()) existing.deleteRecord();

  new GlideSysAttachment().write(gr, inputs.name, contentType || getMimeType(""), inputs.content);

})(inputs, outputs);

function getMimeType(extension) {
  switch (extension) {
    case "html":
      return "text/html";
    case "css":
      return "text/css";
    case "js":
      return "application/javascript";
    case "json":
      return "application/json";
    case "pdf":
      return "application/pdf";
    case "png":
      return "image/png";
    case "jpg":
    case "jpeg":
      return "image/jpeg";
    case "gif":
      return "image/gif";
    case "svg":
      return "image/svg+xml";
    case "ico":
      return "image/x-icon";
    case "mp4":
      return "video/mp4";
    case "mov":
      return "video/quicktime";
    case "avi":
      return "video/x-msvideo";
    case "mp3":
      return "audio/mpeg";
    case "wav":
      return "audio/wav";
    case "ogg":
      return "audio/ogg";
    case "xml":
      return "application/xml";
    default:
      return "text/plain";
  }
}

