sys_variable_value.value:
(function execute(inputs, outputs) {

  var helper = new global.PDIExporterUtils();
  var response = JSON.parse(inputs.responseJSON);

  if(!response.content) {
    throw "Failed to get commit data: " + response.message;
  }

  outputs.file_path = response.path + "";
  outputs.file_name = response.name + "";
  outputs.hash = response.sha;
  outputs.url = response.url;

  try {
    outputs.content = helper.decode(response.content.trim());
  } catch(e) {
    throw "Failed to decode content: " + e.toString(); 
  }

})(inputs, outputs);

